import { __decorate } from "tslib";
import { Directive, ElementRef, HostListener } from '@angular/core';
let NumberCheckDirective = class NumberCheckDirective {
    constructor(elemRef) {
        this.elemRef = elemRef;
    }
    // listens on blur of the input field
    checkNumberValidation() {
        // replace alphabets with empty character
        this.elemRef.nativeElement.value = this.elemRef.nativeElement.value.replace(/[^0-9.]+/g, '');
    }
};
NumberCheckDirective.ctorParameters = () => [
    { type: ElementRef }
];
NumberCheckDirective.propDecorators = {
    checkNumberValidation: [{ type: HostListener, args: ['blur',] }, { type: HostListener, args: ['keyup',] }]
};
