import { __decorate } from "tslib";
import { Injectable } from '@angular/core';
//Required modules
import { HttpClient, HttpHeaders } from '@angular/common/http';
//Environment
import { environment } from 'src/environments/environment';
//rxjs library functions
import { map } from 'rxjs/operators';
import { BehaviorSubject } from 'rxjs';
let ProductService = class ProductService {
    constructor(http) {
        this.http = http;
        // variables
        this.productSearch = new BehaviorSubject('');
        this.currentSearchItem = this.productSearch.asObservable();
    }
    //get product list
    getProducts() {
        let headers = new HttpHeaders();
        headers.append('Content-Type', 'application/json');
        headers = headers.append('Accept', 'application/json');
        return this.http.get(`${environment.apiUrl}product/list`, {
            headers: headers
        })
            .pipe(map((result) => {
            return result.data;
        }));
    }
    //add product
    addProduct(formData) {
        let headers = new HttpHeaders();
        headers.append('Content-Type', 'application/json');
        headers = headers.append('Accept', 'application/json');
        return this.http.post(`${environment.apiUrl}product`, formData, {
            headers: headers
        })
            .pipe(map((result) => {
            return result;
        }));
    }
    //get product by id
    getProductById(id) {
        let headers = new HttpHeaders();
        headers.append('Content-Type', 'application/json');
        headers = headers.append('Accept', 'application/json');
        return this.http.get(`${environment.apiUrl}product/details/${id}`, {
            headers: headers
        })
            .pipe(map((result) => {
            return result.data[0];
        }));
    }
    //update the product
    updateProduct(formData, id) {
        let headers = new HttpHeaders();
        headers.append('Content-Type', 'application/json');
        headers = headers.append('Accept', 'application/json');
        return this.http.patch(`${environment.apiUrl}product/${id}`, formData, {
            headers: headers
        })
            .pipe(map((result) => {
            return result;
        }));
    }
    //delete product
    deleteCategory(id) {
        let headers = new HttpHeaders();
        headers.append('Content-Type', 'application/json');
        headers = headers.append('Accept', 'application/json');
        return this.http.delete(`${environment.apiUrl}product/${id}`, {
            headers: headers
        })
            .pipe(map((result) => {
            return result;
        }));
    }
    // update search item
    updateSearchItem(searchTerm) {
        this.productSearch.next(searchTerm);
    }
};
ProductService.ctorParameters = () => [
    { type: HttpClient }
];
ProductService = __decorate([
    Injectable({
        providedIn: 'root'
    })
], ProductService);
export { ProductService };
